package services;import dao.UserDao;import entity.User;public class UserService {    private UserDao userDao = new UserDao();    public boolean checkUserName(User user) {        if (user.getUserName().equals("") || user.getPassword().equals("")) {            return false;        }        if (userDao.checkUserNam(user.getUserName()) != null) {            return false;        }        return true;    }    public void add(User user) {        userDao.create(user);    }}